CREATE DEFINER=`root`@`localhost` PROCEDURE `GetInventoryTrendMonthly`()
BEGIN
    -- Lấy giá trị tồn kho vào cuối mỗi tháng
    SELECT 
        DATE_FORMAT(last_day_of_month, '%m/%Y') AS label,
        (
            SELECT 
                SUM(p.price * 
                    (
